// client/src/pages/Cart.js - Updated
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Cart = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const { userId } = useParams();

    useEffect(() => {
        const fetchCart = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/cart/${userId}`);
                setCart(response.data);
            } catch (error) {
                console.error('Error fetching cart:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchCart();
    }, [userId]);

    const removeFromCart = async (productId) => {
        try {
            const response = await axios.delete('http://localhost:5001/api/cart/remove', {
                data: { userId, productId }
            });
            setCart(response.data);
        } catch (error) {
            console.error('Error removing item:', error);
        }
    };

    const calculateTotal = () => {
        if (!cart) return 0;
        return cart.products.reduce((total, product) => total + product.price, 0).toFixed(2);
    };

    if (loading) return <div>Loading cart...</div>;
    if (!cart) return <div>Your cart is empty</div>;

    return (
        <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
            <h1>Shopping Cart</h1>
            <div style={{ display: 'grid', gap: '20px' }}>
                {cart.products.map(product => (
                    <div key={product._id} style={{
                        display: 'flex',
                        gap: '20px',
                        padding: '15px',
                        border: '1px solid #ddd',
                        borderRadius: '8px'
                    }}>
                        <img
                            src={product.imageUrl}
                            alt={product.name}
                            style={{ width: '150px', height: '150px', objectFit: 'cover' }}
                        />
                        <div style={{ flex: 1 }}>
                            <h3>{product.name}</h3>
                            <p>{product.description}</p>
                            <p>Price: ${product.price}</p>
                        </div>
                        <button
                            onClick={() => removeFromCart(product._id)}
                            style={{
                                padding: '8px 16px',
                                background: '#ff4444',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                height: '40px',
                                alignSelf: 'center'
                            }}
                        >
                            Remove
                        </button>
                    </div>
                ))}
            </div>
            <div style={{ marginTop: '30px', textAlign: 'right' }}>
                <h2>Total: ${calculateTotal()}</h2>
                <button
                    style={{
                        padding: '12px 24px',
                        background: '#4CAF50',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px'
                    }}
                >
                    Proceed to Checkout
                </button>
            </div>
        </div>
    );
};

export default Cart;
