import React from 'react';

const ProductCard = ({ product, userId, addToCart, addToWishlist }) => {
    return (
        <div style={{ border: '1px solid #ccc', borderRadius: '10px', padding: '15px', textAlign: 'center' }}>
            <img src={product.image} alt={product.name} style={{ width: '100%', height: 'auto', borderRadius: '10px' }} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.price} USD</p>
            <div>
                <button
                    onClick={() => addToCart(product._id)}
                    style={{
                        backgroundColor: '#FF6347', // Tomato red color
                        color: '#fff',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        marginRight: '10px',
                        fontWeight: 'bold',
                    }}
                >
                    Add to Cart
                </button>
                <button
                    onClick={() => addToWishlist(product._id)}
                    style={{
                        backgroundColor: '#20B2AA', // Light sea green color
                        color: '#fff',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontWeight: 'bold',
                    }}
                >
                    Add to Wishlist
                </button>
            </div>
        </div>
    );
};

export default ProductCard;
