import React from 'react';
import { Link } from 'react-router-dom';
import { FaHeart, FaShoppingBag } from 'react-icons/fa';
import logo from '../assets/images/logo.png';
import { jwtDecode } from 'jwt-decode';

const Navbar = () => {
    const token = localStorage.getItem('token');
    let username = '';
    if (token) {
        try {
            const decoded = jwt_decode(token);
            username = decoded.user.username;
        } catch (err) {
            console.error(err);
        }
    }

    const navStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px 20px',
        borderBottom: '1px solid #ccc',
        backgroundColor: 'white',
        fontFamily: '"Montserrat", sans-serif',
    };

    const logoStyle = {
        display: 'flex',
        alignItems: 'center',
    };

    const centerStyle = {
        display: 'flex',
        gap: '20px',
        alignItems: 'center',
    };

    const rightStyle = {
        display: 'flex',
        gap: '15px',
        alignItems: 'center',
    };

    const linkStyle = {
        fontWeight: 'bold',
        color: 'black',
        textDecoration: 'none',
        fontFamily: '"Montserrat", sans-serif',
    };

    const buttonStyle = {
        border: '2px solid black',
        borderRadius: '25px',
        padding: '8px 20px',
        fontFamily: '"Montserrat", sans-serif',
        backgroundColor: 'white',
        color: 'black',
        cursor: 'pointer',
        transition: 'all 0.3s ease',
    };

    return (
        <nav style={navStyle}>
            {/* Left: Nike Logo */}
            <div style={logoStyle}>
                <img src={logo} alt="Nike Logo" style={{ height: '40px' }} />
            </div>

            {/* Center: Navigation Links */}
            <div style={centerStyle}>
                <Link to="/men" style={linkStyle}>Men</Link>
                <Link to="/women" style={linkStyle}>Women</Link>
                <Link to="/kids" style={linkStyle}>Kids</Link>
                <Link to="/jordans" style={linkStyle}>Jordans</Link>
                <Link to="/sport" style={linkStyle}>Sport</Link>
                <Link to="/new" style={linkStyle}>NEW</Link>
            </div>

            {/* Right: Wishlist, Shopping Bag, and Auth Buttons */}
            <div style={rightStyle}>
                <Link to="/wishlist">
                    <FaHeart size={20} style={{ color: 'black' }} />
                </Link>
                <Link to="/bag">
                    <FaShoppingBag size={20} style={{ color: 'black' }} />
                </Link>
                {token ? (
                    <>
                        <span style={{ fontWeight: 'bold' }}>Hello, {username}</span>
                        <Link to="/logout">
                            <button style={buttonStyle}>Logout</button>
                        </Link>
                    </>
                ) : (
                    <>
                        <Link to="/login">
                            <button style={buttonStyle}>Login</button>
                        </Link>
                        <Link to="/signup">
                            <button style={buttonStyle}>Signup</button>
                        </Link>
                    </>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
