import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from '../components/ProductCard';

const MenPage = () => {
    const [categories, setCategories] = useState([]);
    const [selectedCategoryId, setSelectedCategoryId] = useState(null);
    const [products, setProducts] = useState([]);

    // Fetch men's categories on mount
    useEffect(() => {
        axios.get('http://localhost:5001/api/categories/men')
            .then(res => {
                setCategories(res.data);
                if (res.data.length > 0) {
                    // Select the first category by default
                    setSelectedCategoryId(res.data[0]._id);
                }
            })
            .catch(err => console.log(err));
    }, []);

    // Fetch products when selected category changes
    useEffect(() => {
        if (selectedCategoryId) {
            axios.get(`http://localhost:5001/api/products/category/${selectedCategoryId}`)
                .then(res => {
                    // Display only the first 6 products (2 rows x 3 columns)
                    setProducts(res.data.slice(0, 6));
                })
                .catch(err => console.log(err));
        }
    }, [selectedCategoryId]);

    const handleCategoryClick = (categoryId) => {
        setSelectedCategoryId(categoryId);
    };

    return (
        <div style={{ display: 'flex', padding: '20px' }}>
            {/* Left sidebar: Categories */}
            <div style={{ width: '20%', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
                <h2>Categories</h2>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {categories.map(category => (
                        <li
                            key={category._id}
                            onClick={() => handleCategoryClick(category._id)}
                            style={{
                                marginBottom: '10px',
                                cursor: 'pointer',
                                fontWeight: selectedCategoryId === category._id ? 'bold' : 'normal'
                            }}
                        >
                            {category.name}
                        </li>
                    ))}
                </ul>
            </div>
            {/* Right side: Products Grid */}
            <div style={{ width: '80%', paddingLeft: '20px' }}>
                <h1>Men's Products</h1>
                <div
                    className="product-grid"
                    style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(3, 1fr)',
                        gridGap: '20px'
                    }}
                >
                    {products.length > 0 ? (
                        products.map(product => (
                            <ProductCard key={product._id} product={product} userId={null} />
                        ))
                    ) : (
                        <p>No products available for this category.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MenPage;
