import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = ({ userId = "647f9d4a1234567890abcdef" }) => {
    const [cart, setCart] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        if (!userId) {
            setError('User not logged in.');
            return;
        }
        axios.get(`http://localhost:5001/api/cart/${userId}`)
            .then(response => {
                setCart(response.data);
            })
            .catch(err => {
                console.error(err);
                setError('Error fetching cart.');
            });
    }, [userId]);

    const removeFromCart = (productId) => {
        axios.delete('http://localhost:5001/api/cart/remove', { data: { userId, productId } })
            .then(response => {
                setCart(response.data.cart);
            })
            .catch(err => console.error(err));
    };

    const calculateTotal = () => {
        if (!cart || !cart.products) return 0;
        return cart.products.reduce((total, item) => {
            return total + (item.product.price * item.quantity);
        }, 0);
    };

    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Your Cart</h2>
            {cart && cart.products && cart.products.length > 0 ? (
                <div>
                    {cart.products.map(item => (
                        <div key={item.product._id} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
                            <img src={item.product.imageUrl} alt={item.product.name} style={{ width: '100px' }} />
                            <p>{item.product.name}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Price: ${item.product.price}</p>
                            <p>Subtotal: ${item.product.price * item.quantity}</p>
                            <button onClick={() => removeFromCart(item.product._id)}>Remove</button>
                        </div>
                    ))}
                    <h3>Total: ${calculateTotal()}</h3>
                    <button>Checkout</button>
                </div>
            ) : (
                <p>Your cart is empty.</p>
            )}
        </div>
    );
};

export default Cart;
