import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from '../components/ProductCard';

const CartPage = () => {
    const [cart, setCart] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [userId, setUserId] = useState('some-user-id'); // Replace with actual user ID logic

    useEffect(() => {
        const fetchCart = async () => {
            const token = localStorage.getItem('authToken'); // Retrieve the stored token
            try {
                const res = await axios.get('http://localhost:5001/api/cart', {
                    headers: {
                        Authorization: `Bearer ${token}` // Attach the token to the request headers
                    }
                });
                setCart(res.data);
            } catch (err) {
                setError('Failed to load cart');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchCart();
    }, [userId]);

    const removeFromCart = async (productId) => {
        try {
            const response = await axios.post('http://localhost:5001/api/cart/remove', {
                userId: userId,
                productId: productId,
            });
            setCart(response.data);
            alert('Product removed from cart');
        } catch (err) {
            console.error(err);
            alert('Failed to remove product from cart');
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div style={{ padding: '20px' }}>
            <h1>Your Cart</h1>
            <div
                className="product-grid"
                style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(3, 1fr)',
                    gridGap: '20px'
                }}
            >
                {cart.products && cart.products.length > 0 ? (
                    cart.products.map((product) => (
                        <div key={product._id} style={{ display: 'flex', flexDirection: 'column' }}>
                            <ProductCard product={product} />
                            <button onClick={() => removeFromCart(product._id)}>Remove from Cart</button>
                        </div>
                    ))
                ) : (
                    <p>Your cart is empty</p>
                )}
            </div>
        </div>
    );
};

export default CartPage;
