import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CartPage = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    // Replace with your actual user authentication logic
    const userId = 'some-user-id';

    useEffect(() => {
        const fetchCart = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/cart?userId=${userId}`);
                setCart(res.data);
            } catch (err) {
                console.error(err);
                setError('Failed to load cart');
            } finally {
                setLoading(false);
            }
        };
        fetchCart();
    }, [userId]);

    const calculateTotal = () => {
        if (!cart) return 0;
        return cart.products.reduce((total, item) => {
            return total + item.productId.price * item.quantity;
        }, 0);
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;
    if (!cart) return <div>Your cart is empty.</div>;

    return (
        <div style={{ padding: '20px' }}>
            <h1>Your Cart</h1>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                <tr>
                    <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '10px' }}>Product</th>
                    <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '10px' }}>Quantity</th>
                    <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '10px' }}>Price</th>
                    <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '10px' }}>Total</th>
                </tr>
                </thead>
                <tbody>
                {cart.products.map(item => (
                    <tr key={item.productId._id}>
                        <td style={{ padding: '10px' }}>{item.productId.name}</td>
                        <td style={{ padding: '10px' }}>{item.quantity}</td>
                        <td style={{ padding: '10px' }}>${item.productId.price.toFixed(2)}</td>
                        <td style={{ padding: '10px' }}>${(item.productId.price * item.quantity).toFixed(2)}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div style={{ marginTop: '20px', textAlign: 'right' }}>
                <h2>Total: ${calculateTotal().toFixed(2)}</h2>
                <button style={{ padding: '10px 20px', fontSize: '16px', cursor: 'pointer' }}>
                    Checkout
                </button>
            </div>
        </div>
    );
};

export default CartPage;
