import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = ({ userId }) => {
    const [cart, setCart] = useState([]);
    const history = useHistory(); // Access history for redirection

    useEffect(() => {
        // Check if user is logged in by looking for userId in localStorage
        const isLoggedIn = !!localStorage.getItem('userId');

        if (!isLoggedIn) {
            // Redirect to login page if user is not logged in
            history.push('/login');
        } else {
            // Fetch the cart items if the user is logged in
            const userId = localStorage.getItem('userId');
            axios.get(`http://localhost:5001/api/cart/${userId}`)
                .then(res => {
                    setCart(res.data);
                })
                .catch(err => {
                    console.error("Error fetching cart:", err);
                });
        }
    }, [history]); // Dependency on history to ensure it works correctly


    const removeFromCart = (productId) => {
        axios.delete('http://localhost:5001/api/cart/remove', { data: { userId, productId } })
            .then(response => {
                setCart(response.data.cart);
            })
            .catch(err => console.error(err));
    };

    return (
        <div>
            <h2>Your Cart</h2>
            {cart && cart.products && cart.products.length > 0 ? (
                cart.products.map(product => (
                    <div key={product._id}>
                        <img src={product.imageUrl} alt={product.name} style={{ width: '100px' }} />
                        <p>{product.name}</p>
                        <p>{product.description}</p>
                        <p>${product.price}</p>
                        <button onClick={() => removeFromCart(product._id)}>Remove</button>
                    </div>
                ))
            ) : (
                <p>Your cart is empty.</p>
            )}
        </div>
    );
};

export default Cart;
