import React from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProductCard = ({ product, userId }) => {
    const addToCart = () => {
        axios.post('http://localhost:5001/api/cart/add', { productId: product._id, userId })
            .then(response => {
                toast.success(`${product.name} has been added to your cart!`);
            })
            .catch(err => {
                console.error(err);
                toast.error("Failed to add item to cart. Please try again.");
            });
    };

    const addToWishlist = () => {
        axios.post('http://localhost:5001/api/products/add-to-wishlist', { productId: product._id, userId })
            .then(response => {
                toast.success(`${product.name} has been added to your wishlist!`);
            })
            .catch(err => {
                console.error(err);
                toast.error("Failed to add item to wishlist. Please try again.");
            });
    };

    const buttonStyle = {
        border: '2px solid black',
        borderRadius: '25px',
        padding: '8px 20px',
        fontFamily: '"Montserrat", sans-serif',
        backgroundColor: 'white',
        color: 'black',
        cursor: 'pointer',
        transition: 'all 0.3s ease',
    };

    return (
        <div className="product-card" style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
            <img src={product.imageUrl} alt={product.name} style={{ width: '100%', height: 'auto' }} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={addToCart} style={buttonStyle}>Add to Cart</button>
            <button onClick={addToWishlist} style={buttonStyle}>Add to Wishlist</button>
            <ToastContainer position="bottom-right" autoClose={3000} />
        </div>
    );
};

export default ProductCard;
