import React from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProductCard = ({ product, userId }) => {
    const buttonStyle = {
        border: '2px solid black',
        borderRadius: '25px',
        padding: '8px 20px',
        fontFamily: '"Montserrat", sans-serif',
        backgroundColor: 'white',
        color: 'black',
        cursor: 'pointer',
        transition: 'all 0.3s ease',
    };

    const addToCart = () => {
        axios.post('http://localhost:5001/api/cart/add', { productId: product._id, userId })
            .then(() => {
                // Display success toast notification
                toast.success(`${product.name} has been added to your cart!`, {
                    position: "bottom-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            })
            .catch(err => {
                console.error(err);
                // Display error toast notification
                toast.error("Failed to add the product to your cart. Please try again.", {
                    position: "bottom-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            });
    };

    const addToWishlist = () => {
        axios.post('http://localhost:5001/api/products/add-to-wishlist', { productId: product._id, userId })
            .then(() => {
                // Display success toast notification
                toast.success(`${product.name} has been added to your wishlist!`, {
                    position: "bottom-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            })
            .catch(err => {
                console.error(err);
                // Display error toast notification
                toast.error("Failed to add the product to your wishlist. Please try again.", {
                    position: "bottom-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            });
    };

    return (
        <div className="product-card" style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
            <img src={product.imageUrl} alt={product.name} style={{ width: '100%', height: 'auto' }} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={addToCart} style={buttonStyle}>Add to Cart</button>
            <button onClick={addToWishlist} style={buttonStyle}>Add to Wishlist</button>
            {/* ToastContainer must be included for notifications */}
            <ToastContainer />
        </div>
    );
};

export default ProductCard;
