// src/components/ChatBox.js

import React, { useState } from 'react';

const ChatBox = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    const handleSendMessage = () => {
        if (inputMessage.trim()) {
            setMessages([...messages, { sender: 'user', message: inputMessage }]);
            setInputMessage('');

            // Simulate bot response after 1 second
            setTimeout(() => {
                setMessages((prevMessages) => [
                    ...prevMessages,
                    { sender: 'bot', message: 'Hello! How can I help you today?' },
                ]);
            }, 1000);
        }
    };

    return (
        <div
            style={{
                position: 'fixed',
                bottom: '70px',
                right: '20px',
                width: '300px',
                height: '400px',
                backgroundColor: '#fff',
                borderRadius: '8px',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
                display: 'flex',
                flexDirection: 'column',
                padding: '10px',
            }}
        >
            <div
                style={{
                    flex: 1,
                    overflowY: 'auto',
                    marginBottom: '10px',
                    paddingRight: '5px',
                }}
            >
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        style={{
                            display: 'flex',
                            flexDirection: msg.sender === 'user' ? 'row-reverse' : 'row',
                            marginBottom: '10px',
                        }}
                    >
                        <div
                            style={{
                                padding: '8px 12px',
                                backgroundColor: msg.sender === 'user' ? '#4CAF50' : '#f1f1f1',
                                color: msg.sender === 'user' ? '#fff' : '#000',
                                borderRadius: '15px',
                                maxWidth: '70%',
                            }}
                        >
                            {msg.message}
                        </div>
                    </div>
                ))}
            </div>
            <div style={{ display: 'flex' }}>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    style={{
                        flex: 1,
                        padding: '10px',
                        border: '1px solid #ccc',
                        borderRadius: '20px',
                    }}
                />
                <button
                    onClick={handleSendMessage}
                    style={{
                        backgroundColor: '#4CAF50',
                        color: 'white',
                        border: 'none',
                        borderRadius: '50%',
                        padding: '10px',
                        marginLeft: '5px',
                        cursor: 'pointer',
                    }}
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatBox;
